mapper.select:
SELECT COUNT(*) FROM board
AND categoryboard_categoryId = #{categoryId}
AND title LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE board b
JOIN login l ON b.loginLoginNo = l.loginNo
SET
b.title = #{title},
b.content = #{content},
b.categoryboard_categoryId = #{categoryId},
b.isNotice = CASE WHEN l.role = 2 THEN #{isNotice} ELSE b.isNotice END,
b.imagePath = #{imagePath}  
WHERE b.boardno = #{boardno}
AND b.loginLoginNo = #{loginLoginNo};
